package com.android.song.itemlistglide;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.widget.ListView;

import java.util.ArrayList;
import java.util.Date;

public class MainActivity extends AppCompatActivity {

    ListView listView;
    MyListViewAdapter myListViewAdapter;
    ArrayList<Item> arrayList = null;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        listView = (ListView) findViewById(R.id.listView);
        arrayList = new ArrayList<Item>();

        arrayList.add(new Item("data:image/jpeg;base64,", "홍길동", "안녕하세요", new Date(System.currentTimeMillis())));
        arrayList.add(new Item("data:image/jpeg;base64,", "코난", "마취총!", new Date(System.currentTimeMillis())));
        arrayList.add(new Item("data:image/jpeg;base64,", "도라에몽", "단팥빵", new Date(System.currentTimeMillis())));

        // Custom Adapter 객체에 Context와 아이템리스트를 넣어 생성
        myListViewAdapter = new MyListViewAdapter(this, arrayList);
        // ListView에 생성한 Adapter 적용
        listView.setAdapter(myListViewAdapter);

    }
}
